import React, { Component } from "react";
import { Link } from "react-router-dom";
import { deleteDesire } from "../../../actions/desireListActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class Desire extends Component {
  onDeleteClick(desireList_id, pt_id) {
    this.props.deleteDesire(desireList_id, pt_id);
  }
  render() {
    const { desire } = this.props;
    let priorityString;
    let priorityClass;

    switch (desire.tag) {
      case "1":
        tagClass = "bg-danger text-light";
        tagString = "WEALTH";
      case "2":
        tagClass = "bg-danger text-light";
        tagString = "SELF-REALISATION";
      case "3":
        tagClass = "bg-danger text-light";
        tagString = "LOVE";
      case "4":
        tagClass = "bg-danger text-light";
        tagString = "FAMILY";
      case "5":
        tagClass = "bg-danger text-light";
        tagString = "SELF";
      case "6":
        tagClass = "bg-danger text-light";
        tagString = "CREATIVITY";
      case "7":
        tagClass = "bg-danger text-light";
        tagString = "KNOWLEDGE";
      case "8":
        tagClass = "bg-danger text-light";
        tagString = "CAREER";
      case "9":
        tagClass = "bg-danger text-light";
        tagString = "TRAVELLING";
    }

    return (
      <div className="card mb-1 bg-light">
        <div className={`card-header text-primary ${tagClass}`}>
          {desire.summary}
        </div>
        <div className="card-body bg-light">
          <Link
            to={`/updateDesire/${desire.mapIdentifier}/${desire.mapSequence}`}
            className="btn btn-primary"
          >
            View / Update
          </Link>

          <button
            className="btn btn-danger ml-4"
            onClick={this.onDeleteClick.bind(
              this,
              desire.mapIdentifier,
              desire.mapSequence
            )}
          >
            Delete
          </button>
        </div>
      </div>
    );
  }
}

Desire.propTypes = {
  deleteDesire: PropTypes.func.isRequired,
};
export default connect(null, { deleteDesire })(Desire);
