import React, { Component } from "react";
import { Link } from "react-router-dom";
import { deleteDesire } from "../../../actions/desireListActions";
import PropTypes from "prop-types";
import { connect } from "react-redux";

class Desire extends Component {
  onDeleteClick(desireList_id, pt_id) {
    this.props.deleteDesire(desireList_id, pt_id);
  }
  render() {
    const { desire } = this.props;
    let tagString;
    let tagClass;

    switch (desire.tag) {
      case "1":
        tagClass = "bg-danger text-light";
        tagString = "WEALTH";
        break;
      case "2":
        tagClass = "bg-danger text-light";
        tagString = "SELF-REALISATION";
        break;
      case "3":
        tagClass = "bg-danger text-light";
        tagString = "LOVE";
        break;
      case "4":
        tagClass = "bg-danger text-light";
        tagString = "FAMILY";
        break;
      case "5":
        tagClass = "bg-danger text-light";
        tagString = "SELF";
        break;
      case "6":
        tagClass = "bg-danger text-light";
        tagString = "CREATIVITY";
        break;
      case "7":
        tagClass = "bg-danger text-light";
        tagString = "KNOWLEDGE";
        break;
      case "8":
        tagClass = "bg-danger text-light";
        tagString = "CAREER";
        break;
      case "9":
        tagClass = "bg-danger text-light";
        tagString = "TRAVELLING";
        break;
    }

    return (
      <div className="card mb-1 bg-light">
        <div className={`card-header text-primary ${tagClass}`}>
          <h5 className="card-title">{desire.description}</h5>
        </div>
        <div className="card-body">
          <Link
            to={`/updateDesire/${desire.mapIdentifier}/${desire.desireSequence}`}
            className="btn btn-outline-info m-1"
          >
            View / Update
          </Link>

          <button
            className="btn btn-outline-danger m-1"
            onClick={this.onDeleteClick.bind(
              this,
              desire.mapIdentifier,
              desire.desireSequence
            )}
          >
            Delete
          </button>
        </div>
      </div>
    );
  }
}

Desire.propTypes = {
  deleteDesire: PropTypes.func.isRequired,
};
export default connect(null, { deleteDesire })(Desire);
